import React, {useContext} from 'react'
import './Card.css'

export class Card extends React.Component {
    img;
    constructor(props) {
        super(props)
        this.state = {};
    }
    render() {
        let img = <img className={"picture " + this.props.status} src={this.props.src} alt=""/>
        return <div className={'card'}>
            <div className={'item'}>
                {img}
            </div>
        </div>
    }
    componentDidMount() {
        setTimeout(() => {this.img.className="picture false"}, 1000)
    }
}
