import React from 'react'
import './App.css'
import {Table} from "./components/table/Table";
import {Card} from "./components/card/Card";

let num = 16;

class App extends React.Component {
    constructor(props) {
        super(props);
        let arr = [];
        for (let i = 1; i <= num; i++) {
            arr.push(<Card src={i + ".jpg"} key={i}/>);
        }
        this.state = {
            cards: arr,
            status: false
        }
    }
    render() {
        return <Table>
            {this.state.cards}
        </Table>
    }
    ComponentDidMount = () => {
        setTimeout(() => {this.setState(this.state = {status: false})}, 1200)
    };
}

export default App
