import React, {Fragment} from 'react'
import './App.css'
import {Table} from "./components/table/Table";
import {Card} from "./components/card/Card";
import {Timer} from "./components/timer/Timer";
import {Result} from "./components/result/Result";

let num = 16;

class App extends React.Component {
    constructor(props) {
        super(props);
        let arr = [];
        this.state = {
            answers: [1, 2, 7, 11, 14],
            cards: arr,
            flag: 11,
            status: false,
            changeFlag(value) {
                this.setState(this.state = {flag: this.state.flag + value})
            },
            finishGame() {
                this.setState(this.state = {status: true})
            }
        }
        for (let i = 1; i <= num; i++) {
            let answer = 'wrong';
            if (this.state.answers.includes(i)) {
                answer = 'truth';
            }
            arr.push(<Card src={i + ".jpg"} index={i} status={"open"} changeStatus={this.state.changeStatus} answer={answer} changeParentFlag={this.state.changeFlag.bind(this)} key={i}/>);
        }
    }
    render() {
        if (!this.state.status) {
            return <Fragment>
                <input type="button" style={{position: "absolute"}} value="check"
                       onClick={this.state.finishGame.bind(this)}/>
                <Timer/>
                <Table>
                    {this.state.cards}
                </Table>
            </Fragment>
        } else {
            return <Result flag={this.state.flag} num={num}></Result>
        }
    }
}

export default App
