import React, {Fragment} from 'react'
import './App.css'
import {Table} from "./components/table/Table";
import {Card} from "./components/card/Card";
import {Timer} from "./components/timer/Timer";
import {Result} from "./components/result/Result";
import {Level} from "./components/level/Level";
import {Container} from "./components/container/Container";

class App extends React.Component {
    constructor(props) {
        super(props);
        let arr = [];
        let numbers = [];
        let answers = [1, 2, 5, 7, 9]
        this.state = {
            answers: answers,
            cards: arr,
            flag: 11,
            right: 4,
            status: 'start',
            src: numbers,
            level: 0,
            changeFlag(value) {
                this.setState(this.state = {flag: this.state.flag + value})
            },
            startGame(num) {
                this.setState(this.state = {status: "current", level: num});
                for (let i = 1; i <= this.state.level; i++) {
                    let answer = 'wrong';
                    let src = this.state.right;
                    if (answers.includes(i)) {
                        answer = 'truth';
                    } else {
                        while (true) {
                            src = (Math.floor(Math.random() * this.state.level) + 1);
                            if (!numbers.includes(src)) {
                                numbers.push(src);
                                break;
                            }
                        }
                    }
                    arr.push(<Card src={src + ".jpg"} index={i} status={"open"} changeStatus={this.state.changeStatus} answer={answer} level={this.state.level} changeParentFlag={this.state.changeFlag.bind(this)} key={i}/>);
                }
            },
            finishGame() {
                this.setState(this.state = {status: "finish"});
            },
            restartGame() {
                this.setState(this.state = {status: "start"})
            }
        }
    }
    render() {
        if (this.state.status === "start") {
            return <Container>
                <Level name={"легко"} action={this.state.startGame.bind(this, 9)}/>
                <Level name={"средне"} action={this.state.startGame.bind(this, 16)}/>
                <Level name={"сложно"} action={this.state.startGame.bind(this, 25)}/>
            </Container>
        } else if (this.state.status === "current") {
            return <Fragment>
                <input type="button" style={{position: "absolute"}} value="check"
                       onClick={this.state.finishGame.bind(this)}/>
                <Timer/>
                <Table>
                    {this.state.cards}
                </Table>
            </Fragment>
        } else {
            return <Fragment>
                <Result flag={this.state.flag} num={this.state.level}/>
                <input type="button" value="return" onClick={this.state.restartGame.bind(this)}/>
            </Fragment>
        }
    }
}

export default App
