import React, {Fragment, useRef} from 'react'

import {
    createSmartappDebugger,
    createAssistant,
} from "@sberdevices/assistant-client";

import './App.css'
import {Table} from "./components/table/Table";
import {Card} from "./components/card/Card";
import {Timer} from "./components/timer/Timer";
import {Result} from "./components/result/Result";
import {Level} from "./components/level/Level";
import {Container} from "./components/container/Container";

let countOfPictures = 25;

const initializeAssistant = (getState/*: any*/) => {
    if (process.env.NODE_ENV === "development") {
        return createSmartappDebugger({
            token: "eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9..qMpJX8hvSxMQvXFLxFb-sJrUsdrPmHE6juiYO18mnQBoc_hajxFWPNyB0zI2aQV2yMrzk55NmjtZr2lFV7m75cewDXn1BzaQYPHp8pHmm6NA6bm4lADCddV9NqrGLU4MbXrkcAXz1Bd0e0Yu-z4LhwfxAobBZ8-fyYZs_4-9WFTaSOdG5yoxOz8h93OOd88nO4pKS6JKcEDsZCl89Tphaduqtp8Ugna3uc_0Akjtc2BvYqu6_WBoen3Qc1QFQG0HZa7G0iXxzxfRdZCkbOCbHpZPYwni4rCKPfAarC3QwiQMUWBD-QBoLNZIAwIULDbTulfm8T0hcJkfVgPcuDjV-_83RPMFfNugZJtppv3HCJcIpDHyN_j-J9IBn2l9ACKHBJWLEaSPO1_5rfhrwNZG-Hf_5El0rJ5jU0msJh6In_RzH5PJ8LtlIuJrLfQrayoppwM2Bh25AnnQwoqU9rLoXlAzEr3LXcp1WC6E5X5zzbZqbAFqa1dIckcT9_6s4icMJB_jRPc3leXym8D4RAAvQKIYpzYWSRUGEIG0PZ5VWwBAeOdJg7dJnQGPbQFK3K7F4Ma9T0gcSNdbR2996db5LRGTCWdBdhZs58i0KT4QOuvERXR_hX1ytgrpDfXfFKM1pyZrcrEljTZTGYg_N_AvpaaKUId8ZOwrveZQI0RY5L0" ?? "",
            initPhrase: 'Запусти память',
            getState,
        });
    }
    return createAssistant({ getState });
};

export {initializeAssistant};

class App extends React.Component {
    constructor(props) {
        super(props);
        this.assistant = initializeAssistant(() => this.getStateForAssistant() );
        this.assistant.on("data", (event/*: any*/) => {
            console.log(`assistant.on(data)`, event);
            const { action } = event
            this.dispatchAssistantAction(action);
        });
        this.assistant.on("start", (event) => {
            console.log(`assistant.on(start)`, event);
        });
        this.state = {
            cards: [],
            flag: 0,
            status: 'start',
            level: 0,
            changeFlag(value) {
                this.setState(this.state = {flag: this.state.flag + value})
            },
            startGame(level) {
                function random(from, till) {
                    return (Math.floor(Math.random() * (till - from)) + from);
                }
                function changeFlag(value) {
                    this.setState(this.state = {flag: this.state.flag + value})
                }
                let countOfAnswers = random(4, 7);
                let answer = random(0, countOfPictures);
                console.log(answer);
                console.log(countOfAnswers);
                let answersIndexes = new Set();
                let used = new Set([answer]);
                let cards = [];

                for (let i = 0; i < countOfAnswers; i++) {
                    while(true) {
                        let ind = random(0, level);
                        if (!answersIndexes.has(ind)) {
                            answersIndexes.add(ind);
                            break;
                        }
                    }
                }
                this.setState(this.state = {status: "current", flag: level - countOfAnswers, level: level, changeFlag: changeFlag});
                for (let i = 0; i < level; i++) {
                    let isAnswer = 'wrong';
                    let value = 0;
                    if (countOfAnswers !== 0 && answersIndexes.has(i)) {
                        {
                            countOfAnswers--;
                            isAnswer = 'truth';
                            value = answer;
                        }
                    }
                    else {
                        while (true) {
                            value = random(0, countOfPictures);
                            if (!used.has(value)) {
                                used.add(value);
                                break;
                            }
                        }
                    }
                    this.["child1"] = React.createRef();
                    cards.push(<Card src={"https://pictures-for-memory-game.s3.eu-north-1.amazonaws.com/" + (value + 1) + ".jpg"} index={i + 1} status={"open"} answer={isAnswer} level={'level_' + level} changeParentFlag={changeFlag.bind(this)} key={i} ref={this.[child + 'i']} />)

                }
                this.setState(this.state = {cards: cards});
            },
            finishGame() {
                this.setState(this.state = {status: "finish"})
            },
            restartGame() {
                this.setState(this.state = {status: "start"})
            }
        }
    }
    getStateForAssistant () {
        console.log('getStateForAssistant: this.state:', this.state)
        const state = {};
        return state;
    }
    dispatchAssistantAction (action) {
        console.log('dispatchAssistantAction', action);
        if (action) {
            switch (action.type) {
                case "easy_level":
                    this.state.startGame.call(this, 9);
                    return;
                case "middle_level":
                    this.state.startGame.call(this, 16);
                    return;
                case "hard_level":
                    this.state.startGame.call(this, 25);
                    return;
                case "return_back":
                    if (this.state.status === "finish") {
                        this.state.restartGame.call(this);
                    }
                    return;
                case "check_answers":
                    if (this.state.status === "current") {
                        this.state.finishGame.call(this);
                    }
                case "choose_num":
                    this.check.call(this.state.cards[action.num - 1]);
                    return;
            }
        }
    }
    check() {
        if (this.state.checked !== "checked") {
            this.setState(this.state = {checked: "checked"})
            if (this.props.answer === "truth") {
                this.props.changeParentFlag(1);
            } else if (this.props.answer === "wrong") {
                this.props.changeParentFlag(-1);
            }
        }
    }
    render() {
        if (this.state.status === "start") {
            return <Container>
                <Level num={1} name={"легко"}/>
                <Level num={2} name={"средне"}/>
                <Level num={3} name={"сложно"}/>
            </Container>
        } else if (this.state.status === "current") {
            return <Fragment>
                <Timer/>
                <Table>
                    {this.state.cards}
                </Table>
            </Fragment>
        } else {
            return <Fragment>
                <Result flag={this.state.flag} num={this.state.level}/>
            </Fragment>
        }
    }
}

export default App
