import React, {Fragment} from 'react'
import './App.css'
import {Table} from "./components/table/Table";
import {Card} from "./components/card/Card";
import {Timer} from "./components/timer/Timer";

let num = 16;

class App extends React.Component {
    constructor(props) {
        super(props);
        let arr = [];
        this.state = {
            answers: [1, 2, 7, 11, 14],
            cards: arr,
            flag: 11,
            status: 'open',
            changeStatus() {
                this.status = 'close'
            },
            changeFlag(value) {
                this.setState(this.state = {flag: this.state.flag + value})
            },
            finishGame() {
                alert(this.state.flag);
            }
        }
        for (let i = 1; i <= num; i++) {
            let answer = 'wrong';
            if (this.state.answers.includes(i)) {
                answer = 'truth';
            }
            arr.push(<Card src={i + ".jpg"} index={i} changeParentStatus={this.state.changeStatus.bind(this)} status={"open"} changeStatus={this.state.changeStatus} answer={answer} changeParentFlag={this.state.changeFlag.bind(this)} key={i}/>);
        }
    }
    render() {
            return <Fragment>
                <input type="button" style={{position: "absolute"}} value="check" onClick={this.state.finishGame}/>
                <Timer/>
                <Table>
                    {this.state.cards}
                </Table>
            </Fragment>
    }
}

export default App
