import React from 'react'
import './Timer.css'

export class Timer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            time: ''
        };
    }
    render() {
        return <div>
            {this.state.time}
        </div>
    }
    componentDidMount() {
        setTimeout(() => {this.setState(this.state = {time: '3'})}, 7000)
        setTimeout(() => {this.setState(this.state = {time: '2'})}, 1000)
        setTimeout(() => {this.setState(this.state = {time: '1'})}, 1000)
    }
}
