import React from 'react'
import './Timer.css'

export class Timer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            time: ''
        };
    }
    render() {
        return <div className={'timer' + (this.state.time === 1) ? " finished" : ""}>
            {this.state.time}
        </div>
    }
    componentDidMount() {
        let ind = 3;
        let flag = setInterval(() => {
            this.setState(this.state = {time: ind});
            --ind;
            if (ind == 0) {
                clearInterval(flag);
            }
        }, 1000)
    }
}
