import React from 'react'
import './App.css'
import {Table} from "./components/table/Table";
import {Card} from "./components/card/Card";
import {Timer} from "./components/timer/Timer";

let num = 16;

class App extends React.Component {
    constructor(props) {
        super(props);
        let arr = [];
        this.state = {
            cards: arr,
            status: 'open',
            changeStatus() {
                this.status = 'close'
            }
        }
        for (let i = 1; i <= num; i++) {
            arr.push(<Card src={i + ".jpg"} index={i} changeParentStatus={this.state.changeStatus.bind(this)} status={"open"} changeStatus={this.state.changeStatus} key={i}/>);
        }
    }
    render() {
            return 
        <Table>
                    {this.state.cards}
                </Table>
    }
    componentDidMount() {
        if (this.state.status === 'false') {
            for (let i = 1; i <= 3; i++) {
                setTimeout(() => {return <Timer time={i}/>}, 1000);
            }
        }
    }
}

export default App
