import React, {Fragment, useRef} from 'react'

import {
    createSmartappDebugger,
    createAssistant,
} from "@sberdevices/assistant-client";

import './App.css'
import {Table} from "./components/table/Table";
import {Card} from "./components/card/Card";
import {Timer} from "./components/timer/Timer";
import {Result} from "./components/result/Result";
import {Level} from "./components/level/Level";
import {Container} from "./components/container/Container";

let countOfPictures = 25;

const initializeAssistant = (getState/*: any*/) => {
    if (process.env.NODE_ENV === "development") {
        return createSmartappDebugger({
            token: "eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9..k7S3kEIQpIg0QGoxiFk8S8izeIZIEFeuHm192wv2VhlTIq0DsmXwwxiqKL3fuaQrJ6Ngna56em6_A6KDfTbLpKh7_yVqQKYyqkSBXrOPG09XKILxsvTfyy7ivDdszfDHDUiLefe4_de7Qqb3lW751d5jpLyFze9nSt6WtQ54DHD2hDfdr5MtCDvbSUT33PDqRlVydkmnoh8QJzMR6O3YDiJxPHtAspqMRkG17VXrPsBNkzFPPY22FPIKhJDHOOg7vPXQy-2McJ0RB4Vq-sQYNGRo45ZX-NVsPipPvFozWEIHcp6JXUxUV5asiOoo_DVcitLbAFI1ccA3b1QYxU1ztwqo6coSsxXfnz_UKlGQ4MsaHEubHHHkGH3HIC0Y9c7tkiLLanwLG68L43l8KefTuyIrNp9nCLM-sBof4F3F68dEjFDnH7dCEPPc4oQlWHJQCKWRfEp9D1Z1x_xckHONiRk37mV6sL5nc4D_KEcodN1Zh7PmjOnzdns0ii9YapxYwGGQZ_BAnlF00rwhV94iGJ6K5lSzbsSJ-LlWKypN3lqlsbuyZOM37lp3v75dbSG9C90LXpZz0FV0JZIgKPikvPYlJthddJ-fi_lowsLiIv5n_Y33qbbgnXhqOluBGkUMMQ4avoL-t88r-khi83dsbv6q7FZ3Azmp2oldk5VnO4w" ?? "",
            initPhrase: 'Запусти память',
            getState,
        });
    }
    return createAssistant({ getState });
};

class App extends React.Component {
    constructor(props) {
        super(props);
        this.assistant = initializeAssistant({});
        this.state = {
            cards: [],
            flag: 0,
            status: 'start',
            level: 0,
            changeFlag(value) {
                this.setState(this.state = {flag: this.state.flag + value})
            },
            startGame(level) {
                function random(from, till) {
                    return (Math.floor(Math.random() * (till - from)) + from);
                }
                function changeFlag(value) {
                    this.setState(this.state = {flag: this.state.flag + value})
                }
                let countOfAnswers = random(4, 7);
                let answer = random(0, countOfPictures);
                console.log(answer);
                console.log(countOfAnswers);
                let answersIndexes = new Set();
                let used = new Set([answer]);
                let cards = [];

                for (let i = 0; i < countOfAnswers; i++) {
                    while(true) {
                        let ind = random(0, level);
                        if (!answersIndexes.has(ind)) {
                            answersIndexes.add(ind);
                            break;
                        }
                    }
                }
                this.setState(this.state = {status: "current", flag: level - countOfAnswers, level: level, changeFlag: changeFlag});
                for (let i = 0; i < level; i++) {
                    let isAnswer = 'wrong';
                    let value = 0;
                    if (countOfAnswers !== 0 && answersIndexes.has(i)) {
                        {
                            countOfAnswers--;
                            isAnswer = 'truth';
                            value = answer;
                        }
                    }
                    else {
                        while (true) {
                            value = random(0, countOfPictures);
                            if (!used.has(value)) {
                                used.add(value);
                                break;
                            }
                        }
                    }
                    cards.push(<Card src={"https://pictures-for-memory-game.s3.eu-north-1.amazonaws.com/" + (value + 1) + ".jpg"} index={i + 1} status={"open"} answer={isAnswer} level={'level_' + level} changeParentFlag={changeFlag.bind(this)} key={i}/>)

                }
                this.setState(this.state = {cards: cards});
            },
            finishGame() {
                this.setState(this.state = {status: "finish"})
            },
            restartGame() {
                this.setState(this.state = {status: "start"})
            }
        }
    }
    render() {
        if (this.state.status === "start") {
            return <Container>
                <Level num={1} name={"легко"} action={this.state.startGame.bind(this, 9)}/>
                <Level num={2} name={"средне"} action={this.state.startGame.bind(this, 16)}/>
                <Level num={3} name={"сложно"} action={this.state.startGame.bind(this, 25)}/>
            </Container>
        } else if (this.state.status === "current") {
            return <Fragment>
                <input type="button" style={{position: "absolute"}} value="check"
                       onClick={this.state.finishGame.bind(this)}/>
                <Timer/>
                <Table>
                    {this.state.cards}
                </Table>
            </Fragment>
        } else {
            return <Fragment>
                <Result flag={this.state.flag} num={this.state.level}/>
                <input type="button" value="return" onClick={this.state.restartGame.bind(this)} style={{position: 'absolute', top: 0, zIndex: 1001}}/>
            </Fragment>
        }
    }
}

export default App
