import React from 'react'
import './App.css'
import {Table} from "./components/table/Table";
import {Card} from "./components/card/Card";

let num = 16;

class App extends React.Component {
    constructor(props) {
        super(props);
        let arr = [];
        this.state = {
            cards: arr,
            status: "open",
            changeStatus() {
                return setTimeout(() => {this.setState(this.state = {status: "close"}); return this.state.status}, 1000)
            }
        }
        for (let i = 1; i <= num; i++) {
            arr.push(<Card src={i + ".jpg"} status={this.state.status} key={i + this.state.status}/>);
        }
    }
    render() {
            return <Table>
                    {this.state.cards}
                </Table>
    }
}

export default App
