import React, {useContext} from 'react'
import './Card.css'

let img;
export class Card extends React.Component {
    img;
    constructor(props) {
        super(props)
        this.state = {
            status: "close",
            checked: "",
        };
    }
    check() {
        if (this.state.checked !== "checked") {
            this.setState(this.state = {checked: "checked"})
            if (this.props.answer === "truth") {
                this.props.changeParentFlag(1);
            } else if (this.props.answer === "wrong") {
                this.props.changeParentFlag(-1);
            }
        }
    }
    render() {
        img = <img className={"picture " + this.state.status} src={this.props.src} alt=""/>
        return <div className={'card ' + this.props.level}>
            <div className={'item ' + this.props.answer + ' ' + this.state.checked} onClick={this.check.bind(this)}>
                <div className={"index " + this.state.status}>{this.props.index}</div>
                {img}
            </div>
        </div>
    }
    async componentDidMount() {
        await img.onload
        console.log(this.props.changeLoadFlag(1))
        this.props.changeLoadFlag()
        await ('level_' + this.props.changeLoadFlag(1) === this.props.level)
        console.log(this.props.changeLoadFlag(1))
        this.setState(this.state = {status: 'open'})
        setTimeout(() => {this.setState(this.state = {status: "close"})}, 7000)
    }
}
